<template>
  <div v-show="showBox" :class="mode === 'pop' ? 'mask' : ''">
    <div :class="mode === 'pop' ? 'verifybox' : ''" :style="{ 'max-width': parseInt(imgSize.width) + 30 + 'px' }">
      <div v-if="mode === 'pop'" class="verifybox-top">
        <span>{{ $t('请完成安全验证') }}</span>
        <span class="verifybox-close">
          <el-button :title="$t('刷新')" circle @click="refresh">
            <Icon icon="Refresh" />
          </el-button>
          <el-button :title="$t('关闭')" circle @click="closeBox">
            <Icon icon="Close" />
          </el-button>
        </span>
      </div>
      <div class="verifybox-bottom" :style="{ padding: mode == 'pop' ? '0 10px 15px 15px' : '0' }">
        <!-- 验证码容器 -->
        <component
          :is="componentType"
          v-if="componentType"
          ref="instance"
          :captcha-type="captchaType"
          :type="verifyType"
          :figure="figure"
          :arith="arith"
          :mode="mode"
          :v-space="vSpace"
          :explain="$t(explain)"
          :img-size="imgSize"
          :block-size="blockSize"
          :bar-size="barSize"
          :default-img="defaultImg"
        />
      </div>
    </div>
  </div>
</template>
<script type="text/babel">
// 行为验证码组件
import VerifySlide from './Verify/VerifySlide.vue'
import VerifyPoints from './Verify/VerifyPoints.vue'
import defaultImg from '@/assets/images/ajcaptcha.jpg'

export default {
  name: 'AjCaptcha',
  components: { VerifySlide, VerifyPoints },
  props: {
    captchaType: {
      type: String,
      default: '',
      required: true
    },
    figure: {
      type: Number,
      default: 0
    },
    arith: {
      type: Number,
      default: 0
    },
    mode: {
      type: String,
      default: 'pop'
    },
    vSpace: {
      type: Number,
      default: 0
    },
    explain: {
      type: String,
      default() {
        return '向右滑动完成验证'
      }
    },
    imgSize: {
      type: Object,
      default() {
        return {
          width: '360px',
          height: '155px'
        }
      }
    },
    blockSize: {
      type: Object,
      default() {}
    },
    barSize: {
      type: Object,
      default() {}
    }
  },
  data() {
    return {
      // showBox:true,
      clickShow: false,
      // 内部类型
      verifyType: undefined,
      // 所用组件类型
      componentType: undefined,
      // 默认图片
      defaultImg: defaultImg
    }
  },
  computed: {
    instance() {
      return this.$refs.instance || {}
    },
    showBox() {
      if (this.mode === 'pop') {
        return this.clickShow
      } else {
        return true
      }
    }
  },
  watch: {
    captchaType: {
      immediate: true,
      handler(captchaType) {
        switch (captchaType.toString()) {
          case 'blockPuzzle':
            this.verifyType = '2'
            this.componentType = 'VerifySlide'
            break
          case 'clickWord':
            this.verifyType = ''
            this.componentType = 'VerifyPoints'
            break
        }
      }
    }
  },
  mounted() {},
  methods: {
    // 刷新
    refresh() {
      if (this.instance.refresh) {
        this.instance.refresh()
      }
    },
    closeBox() {
      this.clickShow = false
    },
    show() {
      if (this.mode === 'pop') {
        this.clickShow = true
      }
    }
  }
}
</script>
